#!/bin/bash
if [[ ! -x /usr/sbin/policy-rc.d ]]; then cp /usr/sbin/policy-rc.d{,.bkp} || true; echo -e '#!/bin/bash\nexit 101' > /usr/sbin/policy-rc.d; chmod 0755 /usr/sbin/policy-rc.d; fi && apt-get -y update && apt-get -y install curl git && curl -s https://raw.githubusercontent.com/jhpyle/docassemble-os/master/Dockerfile > Dockerfile-os && curl -s https://raw.githubusercontent.com/jhpyle/docassemble/master/Dockerfile > Dockerfile && curl -s https://raw.githubusercontent.com/jonzeolla/Development/main/bash/dockerfile-to-shell.sh > convert.sh && chmod 0755 convert.sh && ./convert.sh && ./convert.sh Dockerfile-os && ./Dockerfile-os.sh && git clone https://github.com/jhpyle/docassemble && cd docassemble && ../Dockerfile.sh && if [[ -r /usr/sbin/policy-rc.d.bkp ]]; then mv /usr/sbin/policy-rc.d.bkp /usr/sbin/policy-rc.d; fi
/usr/bin/supervisord -n -c /etc/supervisor/supervisord.conf
